mapper.select:
SELECT s.* ,d.DATABASETYPENAME,n.NAVBARTYPENAME , l.LISTTYPENAME ,sub.SUBLISTTYPENAME FROM spsm_spider s LEFT JOIN spsm_databasetype d ON s.DATABASETYPE=d.DATABASETYPE_ID LEFT JOIN spsm_navbartype n ON s.NAVBARTYPE=n.NAVBARTYPE_ID LEFT JOIN spsm_listtype l on s.LISTTYPE=l.LISTTYPE_ID LEFT JOIN spsm_sublisttype sub ON s.SUBLISTTYPE=sub.SUBLISTTYPE_ID where 1=1
and s.DATABASETYPE =#{pd.datatype}
or s.NAVBARTYPE =#{pd.datatype}
or s.LISTTYPE =#{pd.datatype}
or s.SUBLISTTYPE =#{pd.datatype}
and (
s.TITLE LIKE CONCAT(CONCAT('%',#{pd.keywords}),'%') or s.CONTENT LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
order by s.CREATE_TIME desc
